mapper.select:

SELECT ifnull(max(user_idx), 0) user_idx
FROM user
WHERE user_id = #{userId}
AND user_pw = #{userPw}

SELECT count(*)
FROM user
WHERE user_id = #{userId}


SELECT user_idx, user_id, user_pw, user_email, user_name, user_local
FROM user
WHERE user_idx = #{userIdx}

mapper.update:

UPDATE user
SET WHERE user_pw = #{userPw}, user_email = #{userEmail}, user_name = #{userName}, user_local = #{userLocal}
where user_idx = #{userIdx}


UPDATE user
SET deleted_yn = 'Y'
WHERE user_idx = #{userIdx}

mapper.select:

SELECT user.user_idx, book.book_idx, book.book_title, book.book_price, book.create_date, book.hit_cnt, book.sold_yn
FROM user, book
WHERE user.user_idx = book.user_idx = #{userIdx}
AND book.deleted_yn = 'N'
ORDER BY book_idx DESC;
