mapper.select:

SELECT count(*)
FROM user
WHERE user_id = #{userId}
AND user_pw = #{userPw}

SELECT count(*)
FROM user
WHERE user_id = #{userId}


SELECT user_idx, user_id, user_pw, user_email, user_name, user_local
FROM user
WHERE user_idx = #{userIdx}

mapper.update:

UPDATE user
SET WHERE user_pw = #{userPw}, user_email = #{userEmail}, user_name = #{userName}, user_local = #{userLocal}
where user_idx = #{userIdx}


UPDATE user
SET deleted_yn = 'Y'
WHERE user_idx = #{userIdx}

mapper.select:

SELECT user.user_idx, book.*, book_recommend.*
FROM user, book, book_recommend
WHERE user.user_idx = book_recommend.user_idx = #{userIdx}
AND book.book_idx = book_recommend.book_idx
AND book.deleted_yn='N';
